class Messages extends Component {
  state = {  
    animate:false,
    messages: [
      {
        title:"title",
        content:"Anyone with an audience. Youtubers, musicians, podcasters, writers, programmers, nonprofits, cosplayers, you name it. More than 500,000 creators and millions of their fans are on Buy Me a Coffee."
      },
      {
        title:"title",
        content:"lorem ipsum"
      },
      {
        title:"title",
        content:"lorem ipsum"
      }
    ]
  } 

  addClass = () => {
    const {animate} = this.state  
    this.setState({animate : !animate})
  }


  render() { 
    const {animate} = this.state  
    return (
      <div className="box">  
      <div onClick={()=>this.addClass()} className="title">
        <div className="titleText">Title</div>
        <div className={animate ? 'anime' : null}>-</div>
      </div>
      <div className="line"></div>
      {animate && <div className="content">
Anyone with an audience. Youtubers, musicians, podcasters, writers, programmers, nonprofits, cosplayers, you name it. More than 500,000 creators and millions of their fans are on Buy Me a Coffee.</div>}
    </div>     
    );
  }
}

export default Messages;
