import React, { Component } from 'react';

class Messages extends Component {
  state = {  
    animate:false,
    messages: [
      {
        title:"title",
        content:"lorem ipsum"
      },
      {
        title:"title",
        content:"lorem ipsum"
      },
      {
        title:"title",
        content:"lorem ipsum"
      }
    ]
  } 

  addClass = () => {
    this.setState({
      animate: !animate
    })
  }


  render() { 
    return (
      <div className="box">  
      <div className="title">
        <div className="titleText">Title</div>
        <div onClick={()=>this.addClass()} className={this.state.animate ? 'anime' : null}>-</div>
      </div>
      <div className="line"></div>
      {/* {toggle && <div className="content">Lorem Ipsum</div>} */}
    </div>     
    );
  }
}

export default Messages;
