import React, { Component } from 'react';

class Messages extends Component {
  state = {  
    animate:false,
    messages: [
      {
        title:"title",
        content:"Anyone with an audience. Youtubers, musicians, podcasters, writers, programmers, nonprofits, cosplayers, you name it. More than 500,000 creators and millions of their fans are on Buy Me a Coffee."
      },
      {
        title:"title",
        content:"Anyone with an audience. Youtubers, musicians, podcasters, writers, programmers, nonprofits, cosplayers, you name it. More than 500,000 creators and millions of their fans are on Buy Me a Coffee."
      },
      {
        title:"title",
        content:"Anyone with an audience. Youtubers, musicians, podcasters, writers, programmers, nonprofits, cosplayers, you name it. More than 500,000 creators and millions of their fans are on Buy Me a Coffee."
      },
    ]
  } 

  addClass = () => {
    const {animate} = this.state  
    this.setState({animate : !animate})
  }


  render() { 
    const {animate,messages} = this.state  
    return (
      <div className="flex">
        {messages.map(message=> 
          <div className="box">  
          <div onClick={()=>this.addClass()} className="title">
            <div className="titleText">{message.title}</div>
            <div className={animate ? 'anime' : null}>T</div>
          </div>
          <div className={`line ${animate ? 'lineAnima' : null}`}></div>
          {animate && <div className="content">{message.content}
          </div>}
        </div>     
          )}
      </div>

    );
  }
}

export default Messages;
