import React, { Component } from 'react';

class Messages extends Component {
  state = {  
    id:1,
    animate:false,
    messages: [
      {
        id:1,
        active:false,
        title:"title",
        content:"Anyone with an audience. Youtubers, musicians, podcasters, writers, programmers, nonprofits, cosplayers, you name it. More than 500,000 creators and millions of their fans are on Buy Me a Coffee."
      },
      {
        id:2,
        active:false,
        title:"title",
        content:"Anyone with an audience. Youtubers, musicians, podcasters, writers, programmers, nonprofits, cosplayers, you name it. More than 500,000 creators and millions of their fans are on Buy Me a Coffee."
      },
      {
        id:3,
        active:false,
        title:"title",
        content:"Anyone with an audience. Youtubers, musicians, podcasters, writers, programmers, nonprofits, cosplayers, you name it. More than 500,000 creators and millions of their fans are on Buy Me a Coffee."
      },
    ]
  } 

  addClass = (id) => {
    const message = this.state.messages.find(message=> message.id==id)
    message.active=!message.active;
    this.setState({message})
  }


  render() { 
    const {animate,messages,id} = this.state  
    return (
      <div className="flex">
        {messages.map(message=> 
          <div key={message.id} className="box">  
          <div onClick={()=>this.addClass(message.id)} className="title">
            <div className="titleText">{message.title}</div>
            <div className={message.active ? 'anime' : null}>T</div>
          </div>
          <div className={`line ${message.active ? 'lineAnima' : null}`}></div>
          {message.active && <div className="content">{message.content}
          </div>}
        </div>     
          )}
      </div>

    );
  }
}

export default Messages;
